reports.query:
MATCH (c:Cuisine)
WHERE toLower(c.name) = toLower($neodash_cuisine_name)

// get all ingredients for that cuisine
OPTIONAL MATCH (c)-[r1:USES]->(i:Ingredient)
WITH c, r1, i
WHERE i IS NOT NULL

// randomly sample ingredients
WITH c, r1, i
ORDER BY rand()
LIMIT 30

// connect brands for all sampled ingredients
OPTIONAL MATCH (i)-[r2:ASSOCIATED_WITH]->(b:Brand)
RETURN c, r1, i, r2, b;

MATCH (r:Region)-[:HAS_CUISINE]->(c:Cuisine)-[:HAS_DISH]->(d:Dish)
RETURN r.name AS Region, COUNT(DISTINCT d) AS TotalDishes
ORDER BY TotalDishes DESC;

MATCH (c:Cuisine)-[:HAS_DISH]->(:Dish)-[:USES]->(i:Ingredient)
RETURN c.name AS Cuisine, COUNT(DISTINCT i) AS TotalUniqueIngredients
ORDER BY TotalUniqueIngredients DESC;
